import 'package:flutter/material.dart';import 'package:flutter_secret_chat/widgets/agenda_interface/custom_date_time_picker.dart';import 'package:flutter_secret_chat/widgets/agenda_interface/custom_modal_action_button.dart';import 'package:flutter_secret_chat/widgets/agenda_interface/custom_textfield.dart';import 'package:intl/intl.dart';class   AddTaskPage extends StatefulWidget {  @override  _AddTaskPageState createState() => _AddTaskPageState();}class _AddTaskPageState extends State<AddTaskPage> {   DateFormat dateFormat = DateFormat('yyyy-MM-dd h:mm a');   DateFormat militarFomat = DateFormat('yyyy-MM-dd HH:mm:ss');  /////////////////////////    PICKERS  ///////////////////////////////////////////////////////  DateTime _selectedDate = new DateTime.now();//  String _selectedDate = "Seleccionar fecha";  String _selectedTime= "Seleccionar hora";  //CALENDARIO  Future _pickDate() async{//    var format = DateFormat.yMd('es');    DateTime datepick = await showDatePicker(context: context, initialDate: new DateTime.now(), locale: const Locale('es','ES'),        firstDate: new DateTime.now().add(Duration(days: -365)),        lastDate: new DateTime.now().add(Duration(days: 365)));    if(datepick != null){      setState(() {        _selectedDate = datepick;      });    }  }  Future _pickTime() async{    TimeOfDay timepick = await showTimePicker(context: context, initialTime: TimeOfDay.now());    if(timepick != null) {      setState(() {        _selectedTime = timepick.toString();      });    }  }  ////////////////////////////////////////////////////////////////////////////////////////////  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.all(24.0),      child: Column(        mainAxisSize: MainAxisSize.min,        children: <Widget>[          Center(child: Text("Anadir tarea", style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16),)),          SizedBox(height: 24),          CustomTexfield(labelText: "Ingresa el nombre de la tarea"),          SizedBox(height: 12),          ///Calendario          CustomDateTimePiker(icon: Icons.date_range, onPressed: _pickDate, value: new DateFormat("dd-MM-yyy").format(_selectedDate)),          ///Reloj          CustomDateTimePiker(icon: Icons.access_time, onPressed: _pickTime, value: _selectedTime),          SizedBox(height: 24), ///-------[ CLOSE SESSION ]--------------///          CustomModalActionButton(onClose: (){Navigator.of(context).pop();},                                     onSave: (){//                                       String fecha = militarFomat.format(selectedDate).toString();                                     },)///--[ SAVE ]--////          _actionButton(context)        ],      ),    );  }}